import React from "react";
import NestedModal from "./Nested";

const Modal = ({isOpen, onClose, children}) => {
    if(!isOpen) return null;

    const openModal = () => {
        setIsModalOpen(true);
        setIsNestedModalOpen(false);
      };
      
      const openNestedModal = () => {
        setIsNestedModalOpen(true);
        setIsModalOpen(false);
      };
      

    return (
        <div className="modal">
            <div className="modal_contant">
                { children }
                <button onClick={onClose}>Cerrar</button>
                <button onClick={openNestedModal}>Abrir Ventana Modal Anidada</button>
                <NestedModal isOpen={isNestedModalOpen} onClose={closeNestedModal}>
                    <h3>Ventana Modal Anidada</h3>
                    <p>Esta es una ventana modal anidada.</p>
                </NestedModal>
            </div>
        </div>
    )
}

export default Modal;
