import React, { useState } from 'react';

const DataStore = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [cartItems, setCartItems] = useState([]);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setSearchQuery('');
    setIsOpen(false);
  };

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const cards = [
    {
      id: 1,
      title: 'Tarjeta 1',
      category: 'Categoría 1',
      description: 'Esta es la descripción de la Tarjeta 1',
      image: 'imagen1.jpg',
    },
    // ... Otras tarjetas ...
  ];

  const filteredCards = cards.filter((card) => {
    if (selectedCategory && card.category !== selectedCategory) {
      return false;
    }
    if (searchQuery && !card.title.toLowerCase().includes(searchQuery.toLowerCase())) {
      return false;
    }
    return true;
  });

  return (
    <div>
      <div className="dropdown">
        <button className="dropdown-toggle" onClick={toggleDropdown}>
        Categorías
      </button>
      {isOpen && (
        <ul className="dropdown-menu">
          <li onClick={() => handleCategorySelect('Categoría 1')}>Ropa</li>
          <li onClick={() => handleCategorySelect('Categoría 2')}>Accesorios</li>
          <li onClick={() => handleCategorySelect('Categoría 3')}>Energizantes</li>
        </ul>
      )}
      {selectedCategory && (
        <div className="selected-category">
          <h3>Tarjetas de la categoría: {selectedCategory}</h3>
          <div className="card-container">
            {filteredCards.length > 0 ? (
              filteredCards.map((card) => (
                <div className="card" key={card.id}>
                  <h4>{card.title}</h4>
                  <p>{card.description}</p>
                  <img src={card.image} alt={card.title} />
                  <button onClick={() => addToCart(card)}>Agregar al carrito</button>
                </div>
              ))
            ) : (
              <p>No hay tarjetas disponibles.</p>
            )}
          </div>
        </div>
      )}
      <div className="cart">
        <h3>Carrito de compras</h3>
        {cartItems.length > 0 ? (
          <ul>
            {cartItems.map((item, index) => (
              <li key={index}>
                <h4>{item.title}</h4>
                <p>{item.description}</p>
                <img src={item.image} alt={item.title} />
              </li>
            ))}
          </ul>
        ) : (
          <p>El carrito está vacío.</p>
        )}
      </div>
    </div>
  </div>
  );
};

export default DataStore;
