import React, { useState } from 'react';

const CardComponent = () => {
  const [selectedButton, setSelectedButton] = useState(null);
  const [cards, setCards] = useState([]);

  const handleButtonClick = (buttonId) => {
    setSelectedButton(buttonId);
    const filteredCards = data.filter((card) => card.buttonId === buttonId);
    setCards(filteredCards);
  };

  const data = [
    { id: 1, buttonId: 'button1', content: 'Contenido de la tarjeta 1' },
    { id: 2, buttonId: 'button2', content: 'Contenido de la tarjeta 2' },
    { id: 3, buttonId: 'button1', content: 'Contenido de la tarjeta 3' },
    { id: 4, buttonId: 'button3', content: 'Contenido de la tarjeta 4' },
  ];

  return (
    <div>
      <button onClick={() => handleButtonClick('button1')}>Botón 1</button>
      <button onClick={() => handleButtonClick('button2')}>Botón 2</button>
      <button onClick={() => handleButtonClick('button3')}>Botón 3</button>
      <div>
        {cards.map((card) => (
          <div key={card.id}>Tarjeta: {card.content}</div>
        ))}
      </div>
    </div>
  );
};

export default CardComponent;
