import React, { useState } from "react";

const CardComponent = () => {
    const [selectButton, setSelectButton] = useState(null);

    const handleButtonClick = (buttonId) => {
        setSelectButton(buttonId);
    }

    const renderCards = () => {
        const data = [
            { id: 1, buttonId: 'button1', content: 'Contenido de la carta 1'},
            { id: 2, buttonId: 'button2', content: 'Contenido de la carta 2'},
            { id: 3, buttonId: 'button3', content: 'Contenido de la carta 3'},
            { id: 4, buttonId: 'button4', content: 'Contenido de la carta 4'}
        ]

        const filteredCards = data.filter((card) => card.buttonId === selectButton);

        return filteredCards.map((card) => {
            <div key={card.id}>Tarjeta: {card.content}</div>
        })
    }

    return (
        <div>
          <button onClick={() => handleButtonClick('button1')}>Botón 1</button>
          <button onClick={() => handleButtonClick('button2')}>Botón 2</button>
          <button onClick={() => handleButtonClick('button3')}>Botón 3</button>
          {renderCards()}
        </div>
    );
}

export default CardComponent;
