import React, { useState } from 'react';
import { Cards } from '../Ui/Cards';

const CardComponent = () => {
  const [selectedButton, setSelectedButton] = useState(null);
  const [numCards, setNumCards] = useState(0);

  const handleButtonClick = (buttonId) => {
    setSelectedButton(buttonId);
  };
  const handleClick = () => {
    setNumCards(numCards + 2);
  }

  const renderCards = () => {
    // Aquí puedes reemplazar "data" con tu propio conjunto de datos de tarjetas
    const data = [
      { id: 1, buttonId: 'button1', content: <div className='store-cards'><Cards/><Cards/></div> },
      { id: 2, buttonId: 'button2', content: <div className='store-cards'><Cards/><Cards/></div> },
      { id: 3, buttonId: 'button3', content: <div className='store-cards'><Cards/><Cards/></div> },
      { id: 4, buttonId: 'button4', content: <div className='store-cards'><Cards/><Cards/></div> },
    ];

    // Filtra las tarjetas basadas en el botón seleccionado
    const filteredCards = selectedButton
      ? data.filter((card) => card.buttonId === selectedButton)
      : [];

    return filteredCards.map((card) => (
      <div key={card.id}>{card.content}</div>
    ));

  };

  const renderClick = () => {
    const cards = [];
    for (let i = 1; i <= numCards; i++) {
      cards.push(<div key={i}><Cards/></div>);
    }
    return cards;
  }

  return (
    <>
    <div className='buttons'>
      <button className='buttonCards' onClick={() => handleButtonClick('button1')}>Botón 1</button>
      <button className='buttonCards' onClick={() => handleButtonClick('button2')}>Botón 2</button>
      <button className='buttonCards' onClick={() => handleButtonClick('button3')}>Botón 3</button>
      <button className='buttonCards' onClick={() => handleButtonClick('button4')}>Botón 4</button>
    </div>
    <div className='store-cards'>{renderCards()}</div>
    <div className="card-grid">{renderClick()}</div>
        <button cla onClick={handleClick}>Mostrar más cartas</button>

    </>
  );
};

export default CardComponent;
