import React, {useState,useRef,useEffect,useContext} from 'react';
import Modal from 'react-modal';
import { useNavigate } from 'react-router-dom'
import axios from '../api/axios';

const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;
const REGISTER_URL = '/register';



const ModalWindow = () => {
    let subtitle;
    const [isFirstModalOpen, setIsFirstModalOpen] = useState(false);
    const [isSecondModalOpen, setIsSecondModalOpen] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const navigate = useNavigate();

    const handleLogin = () => {
        setIsLoggedIn(true);
        navigate('/home')
    }

    const openFirstModal = () => {
        setIsFirstModalOpen(true)
        setIsSecondModalOpen(false);
    }
    const closeFirstModal = () => {
        setIsFirstModalOpen(false);
    }  

    function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#FFFF00';
    subtitle.style.textAlign = 'center';
  }

  const openSecondModal = () => {
    setIsSecondModalOpen(true);
    setIsFirstModalOpen(false);
}
const closeSecondModal = () => {
    setIsSecondModalOpen(false);
}

const closeModal = () => {
    setIsFirstModalOpen(false)
    setIsSecondModalOpen(false)
}

    const userRef = useRef();
    const errRef = useRef();

    const [user,setUser] = useState('');
    const [validName,setValidName] = useState(false);

    const [name, setName] = useState('');

    const [phone,setPhone] = useState(''); 

    const [email,setEmail] = useState('');
 
    const [pwd, setPwd] = useState('');


    const [matchPwd, setMatchPwd] = useState('');

    const [errMsg, setErrMsg] = useState('');


    useEffect(() => {
        setValidName(USER_REGEX.test(user))
    }, [user])

    // useEffect(() => {
    //     setValidPwd(PWD_REGEX.test(pwd));
    //     setValidMatch(pwd === matchPwd);
    // }, [pwd, matchPwd])

    useEffect(() => {
        setErrMsg('');
    }, [user, pwd, matchPwd])

    const handleSubmit = async (e) => {
        e.preventDefault();
        const v1 = USER_REGEX.test(user);
        const v2 = PWD_REGEX.test(pwd);

        if (!v1 || !v2) {
            setErrMsg("Invalid Entry");
            return;
        }
        try {
            const response = await axios.post(REGISTER_URL,
                JSON.stringify({ user,name,phone,email,pwd }),
                {
                    headers: { 'Content-Type': 'application/json' },
                    withCredentials: true
                }
            );
            console.log(JSON.stringify(response?.data));
            setUser('');
            setName('');
            setPhone('');
            setEmail('');
            setPwd('');
            setMatchPwd('');
            
        } catch (err) {
            if (!err?.response) {
                setErrMsg('No Server Response');
            } else if (err.response?.status === 409) {
                setErrMsg('Username Taken');
            } else {
                setErrMsg('Registration Failed')
            }
            errRef.current.focus();
        }
    }

  return (
    <div>
        <a onClick={openFirstModal}>Ingresar /</a>
        <a onClick={openSecondModal}> Crear Cuenta</a>
      
        <Modal className='modal1' isOpen={isFirstModalOpen} onClose={closeFirstModal} onAfterOpen={afterOpenModal}>
            <form className="form">
                <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Iniciar Sesión</h2>
                <div className="field">
                    <svg className='input-icon' xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-person" viewBox="0 0 16 16">
                        <path d="M8 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6Zm2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0Zm4 8c0 1-1 1-1 1H3s-1 0-1-1 1-4 6-4 6 3 6 4Zm-1-.004c-.001-.246-.154-.986-.832-1.664C11.516 10.68 10.289 10 8 10c-2.29 0-3.516.68-4.168 1.332-.678.678-.83 1.418-.832 1.664h10Z"/>
                    </svg>
                    <input autocomplete="off" placeholder="Nombre Usuario" className="input-field" type="text"/>
                </div>
                <div className="field">
                    <svg className="input-icon" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
                        <path d="M8 1a2 2 0 0 1 2 2v4H6V3a2 2 0 0 1 2-2zm3 6V3a3 3 0 0 0-6 0v4a2 2 0 0 0-2 2v5a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V9a2 2 0 0 0-2-2z"></path>
                    </svg>
                    <input placeholder="Contraseña" className="input-field" type="password"/>
                </div>
                <div className="btn">
                    <button onClick={handleLogin} className="button1">Ingresar</button>
                    <button onClick={openSecondModal} className="button2">Registrarse</button>
                </div>
                <button class="button4">¿Olvidaste tu contraseña?</button>
            </form>
            <a className='close' onClick={closeModal}>Cerrar</a>
        </Modal>
        <Modal style={{border: 'none'}} className='modal2' isOpen={isSecondModalOpen} onClose={closeSecondModal} onAfterOpen={afterOpenModal}>
        <form className="form" onSubmit={handleSubmit}>
        <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
                <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Registrarse</h2>
                <div className="field">
                    <svg className='input-icon' xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-person" viewBox="0 0 16 16">
                        <path d="M8 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6Zm2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0Zm4 8c0 1-1 1-1 1H3s-1 0-1-1 1-4 6-4 6 3 6 4Zm-1-.004c-.001-.246-.154-.986-.832-1.664C11.516 10.68 10.289 10 8 10c-2.29 0-3.516.68-4.168 1.332-.678.678-.83 1.418-.832 1.664h10Z"/>
                    </svg>
                    <input autocomplete="off" placeholder="Nombre Usuario" className="input-field" type="text" id="username" ref={userRef} onChange={(e) => setUser(e.target.value)} value={user} required/>
                    <p id="uidnote" className={userFocus && user && !validName ? "instructions" : "offscreen"}>
                                    <FontAwesomeIcon icon={faInfoCircle} />
                                    4 a 24 caracteres.<br />
                                    Debe comenzar con una letra.<br />
                                    Se permiten letras, números, guiones bajos y guiones.
                                </p>
                </div>
                <div className="field">
                    <svg className='input-icon' xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-person" viewBox="0 0 16 16">
                        <path d="M8 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6Zm2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0Zm4 8c0 1-1 1-1 1H3s-1 0-1-1 1-4 6-4 6 3 6 4Zm-1-.004c-.001-.246-.154-.986-.832-1.664C11.516 10.68 10.289 10 8 10c-2.29 0-3.516.68-4.168 1.332-.678.678-.83 1.418-.832 1.664h10Z"/>
                    </svg>
                    <input autocomplete="off" placeholder="Nombre y Apellidos" className="input-field" type="text" id='name' ref={userRef}  onChange={(e) => setName(e.target.value)} value={name}/>
                </div>
                <div className="field">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-telephone" viewBox="0 0 16 16">
                        <path d="M3.654 1.328a.678.678 0 0 0-1.015-.063L1.605 2.3c-.483.484-.661 1.169-.45 1.77a17.568 17.568 0 0 0 4.168 6.608 17.569 17.569 0 0 0 6.608 4.168c.601.211 1.286.033 1.77-.45l1.034-1.034a.678.678 0 0 0-.063-1.015l-2.307-1.794a.678.678 0 0 0-.58-.122l-2.19.547a1.745 1.745 0 0 1-1.657-.459L5.482 8.062a1.745 1.745 0 0 1-.46-1.657l.548-2.19a.678.678 0 0 0-.122-.58L3.654 1.328zM1.884.511a1.745 1.745 0 0 1 2.612.163L6.29 2.98c.329.423.445.974.315 1.494l-.547 2.19a.678.678 0 0 0 .178.643l2.457 2.457a.678.678 0 0 0 .644.178l2.189-.547a1.745 1.745 0 0 1 1.494.315l2.306 1.794c.829.645.905 1.87.163 2.611l-1.034 1.034c-.74.74-1.846 1.065-2.877.702a18.634 18.634 0 0 1-7.01-4.42 18.634 18.634 0 0 1-4.42-7.009c-.362-1.03-.037-2.137.703-2.877L1.885.511z"/>
                    </svg>
                    <input autocomplete="off" placeholder="Telefono" className="input-field" type="text" id='phone' onChange={(e) => setPhone(e.target.value)} value={phone}/>
                </div>
                <div className="field">
                    <svg className="input-icon" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
                        <path d="M13.106 7.222c0-2.967-2.249-5.032-5.482-5.032-3.35 0-5.646 2.318-5.646 5.702 0 3.493 2.235 5.708 5.762 5.708.862 0 1.689-.123 2.304-.335v-.862c-.43.199-1.354.328-2.29.328-2.926 0-4.813-1.88-4.813-4.798 0-2.844 1.921-4.881 4.594-4.881 2.735 0 4.608 1.688 4.608 4.156 0 1.682-.554 2.769-1.416 2.769-.492 0-.772-.28-.772-.76V5.206H8.923v.834h-.11c-.266-.595-.881-.964-1.6-.964-1.4 0-2.378 1.162-2.378 2.823 0 1.737.957 2.906 2.379 2.906.8 0 1.415-.39 1.709-1.087h.11c.081.67.703 1.148 1.503 1.148 1.572 0 2.57-1.415 2.57-3.643zm-7.177.704c0-1.197.54-1.907 1.456-1.907.93 0 1.524.738 1.524 1.907S8.308 9.84 7.371 9.84c-.895 0-1.442-.725-1.442-1.914z"></path>
                    </svg>
                    <input autocomplete="off" placeholder="Correo" className="input-field" type="text" id='email' ref={userRef} onChange={(e) => setEmail(e.target.value)} value={email}/>
                </div>
                <div className="field">
                    <svg className="input-icon" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
                        <path d="M8 1a2 2 0 0 1 2 2v4H6V3a2 2 0 0 1 2-2zm3 6V3a3 3 0 0 0-6 0v4a2 2 0 0 0-2 2v5a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V9a2 2 0 0 0-2-2z"></path>
                    </svg>
                    <input placeholder="Contraseña" className="input-field" type="password" id='password' onChange={(e) => setPwd(e.target.value)} value={pwd} required/>
                </div>
                <div className="field">
                    <svg className="input-icon" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
                        <path d="M8 1a2 2 0 0 1 2 2v4H6V3a2 2 0 0 1 2-2zm3 6V3a3 3 0 0 0-6 0v4a2 2 0 0 0-2 2v5a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V9a2 2 0 0 0-2-2z"></path>
                    </svg>
                    <input placeholder="Confirmación de contraseña" className="input-field" type="password" id='confirm_pwd' onChange={(e) => setMatchPwd(e.target.value)} value={matchPwd} required/>
                </div>
                <div className="btn">
                    <button className="button1">Crear Cuenta</button>
                    <button onClick={openFirstModal} className="button2">Iniciar Sesión</button>
                </div>
                <button class="button4">¡Registra tu Gimnasio!</button>
            </form>
            <a className='close' onClick={closeModal}>Cerrar</a>
        </Modal>
    </div>
  );
}

export default ModalWindow;
