import React, {  createContext, useEffect, useState  } from 'react';
import StoreCards from '../../Ui/Cards/StoreCards';
import axios from "axios";


const DataStore = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [cartItems, setCartItems] = useState([]);   
  const [showCartModal, setShowCartModal] = useState(false);
  const [totalPrice, setTotalPrice] = useState(0);
  const [showSecondModal, setShowSecondModal] = useState(false);
  const [products, setProducts] = useState([]);

  const getProducts = async () => {
    await axios
      .get("http://localhost:4000/products")
      .then(({ data }) => setProducts(data.products));
  };

  const getProductsCart = async () => {
    return await axios
      .get("http://localhost:4000/products-cart")
      .then(({ data }) => setCartItems(data.productsCart))
      .catch((error) => console.error(error));
  };

  useEffect(() => {
    getProducts();
    getProductsCart();
  }, []);


  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setSearchQuery('');
    setIsOpen(false);
  };

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  const addItemToCart = async (product) => {
    const { name, img, price } = product;

    await axios.post("http://localhost:3500/products-cart", { name, img, price });

    getProducts();
    getProductsCart();
  };


  const editItemToCart = async (id, query, amount) => {
