import React, { useState } from 'react';

const Components = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [showFilteredResult, setShowFilteredResult] = useState(false);

  const handleOptionClick = (option) => {
    if (selectedOptions.includes(option)) {
      setSelectedOptions(selectedOptions.filter((item) => item !== option));
    } else {
      setSelectedOptions([...selectedOptions, option])
    }
  }

  const options = [
    { id: 1, name: 'Opci贸n 1', cards: ['Tarjeta 1', 'Tarjeta 2', 'Tarjeta 3'] },
    { id: 2, name: 'Opci贸n 2', cards: ['Tarjeta 4', 'Tarjeta 5', 'Tarjeta 6'] },
  ];
  
  return (
    <div>
      {options.map((option) => (
        <div key={option.id} onClick={() => handleOptionClick(option.id)}>
          <h3>{option.name}</h3>
          {selectedOptions.includes(option.id) && (
            <div>
              {option.cards.map((card) => (
                <div key={card}>{card}</div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
  
}

export default Components;
