// export default CardComponent;

const Components = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleOptionClick = (option) => {
    if (selectedOptions.indexOf(option)) {
      setSelectedOptions(selectedOptions.filter((item) => item !== option));
    } else {
      setSelectedOptions([...selectedOptions, option])
    }
  }

  const options = [
    {
      id: 1,
      name: 'Pierna',
      cards: ['Tarjeta 1', 'Tarjeta 2', 'Tarjeta 3'],
    },
    {
      id: 2,
      name: 'Brazo',
      cards: ['Tarjeta 4', 'Tarjeta 5', 'Tarjeta 6'],
    },
    {
      id: 3,
      name: 'Espalda',
      cards: ['Tarjeta 7', 'Tarjeta 8', 'Tarjeta 9'],
    },
    {
      id: 4,
      name: 'Glúteo',
      cards: ['Tarjeta 10', 'Tarjeta 11', 'Tarjeta 12'],
    },
  ]

  return (
    <div>
      {options.map((option) => (
        <div key={option.id} onClick={() => handleOptionClick(option.id)}>
          <h3>{option.name}</h3>
          {selectedOptions.includes(option.id) && (
            <div>
              {option.cards.map((card) => (
                <div key={card}>{card}</div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
  
}
