import React, { useState } from "react";

const Modal = ({isOpen, onClose, children}) => {
    if(!isOpen) return null;

    return (
        <div className="modal">
            <div className="modal_contant">
                { children }
                <button onClick={onClose}>Cerrar</button>
            </div>
        </div>
    )
}

const Ventana = () => {
    const [isFirstModalOpen, setIsFirstModalOpen] = useState(false)
    const [isSecondModalOpen, setIsSecondModalOpen] = useState(false)

    const openFirstModal = () => {
        setIsFirstModalOpen(true)
    }
    const closeFirstModal = () => {
        setIsFirstModalOpen(false)
    }

    const openSecondModal = () => {
        setIsSecondModalOpen(true)
    }
    const closeSecondModal = () => {
        setIsSecondModalOpen(false)
    }

    return (
        <div>
            <button onClick={openFirstModal}>Abrir 1 ventana</button>
            <Modal isOpen={isFirstModalOpen} onClose={closeFirstModal}>
                <h1>Primera Ventana</h1>
                <button onClick={openSecondModal}>Abrir 2 ventana</button>
            </Modal>
            <Modal isOpen={isSecondModalOpen} onClose={closeSecondModal}>
                <h2>Segunda Ventana</h2>
                <button onClick={openFirstModal}>Abrir 1 ventana</button>             
            </Modal>
        </div>
    )
}

export default Ventana;
