import React, { useState } from "react";

const Modal = ({ isOpen, onClose, children }) => {
    if (!isOpen) return null;

    return (
        <div className="modal">
            <div className="modal_content">
                { children }
                <button onClick={onClose}>Cerrar</button>
            </div>
        </div>
    )
}

const WindowModal = () => {
    const [ activeModal, setActiveModal ] = useState(null);

    const openModal = (modalName) => {
        setActiveModal(modalName);
    }

    const closeModal = () => {
        setActiveModal(null);
    }

    return (
        <div>
            <button onClick={() => openModal('modal1')}>Abrir ventana mosal 1</button>
            <button onClick={() => openModal('modal2')}>Abrir ventana modal 2</button>

            <Modal isOpen={activeModal === 'modal1'} onClose={closeModal}>
                <h1>Ventana modal 1</h1>
                <p>Contenido</p>
                <button onClick={() => openModal('modal2')}>Abrir ventana 2</button>
            </Modal>

            <Modal isOpen={activeModal === 'modal2'} ></Modal>
        </div>
    )
}

export default WindowModal;
