import React, { useState } from 'react';

const CardComponent = () => {
  const [selectedButton, setSelectedButton] = useState(null);

  const handleButtonClick = (buttonId) => {
    setSelectedButton(buttonId);
  };

  const renderCards = () => {
    // Aquí puedes reemplazar "data" con tu propio conjunto de datos de tarjetas
    const data = [
      { id: 1, buttonId: 'button1', content: 'Contenido de la tarjeta 1' },
      { id: 2, buttonId: 'button2', content: 'Contenido de la tarjeta 2' },
      { id: 3, buttonId: 'button3', content: 'Contenido de la tarjeta 3' },
      { id: 4, buttonId: 'button4', content: 'Contenido de la tarjeta 4' },
    ];

    // Filtra las tarjetas basadas en el botón seleccionado
    const filteredCards = data.filter((card) => card.buttonId === selectedButton);

    return filteredCards.map((card) => (
      <div key={card.id}>Tarjeta: {card.content}</div>
    ));
  };

  return (
      <div className='wrapper'>
        <div className='option'>
            <input className='input' type='radio' name='btn' value='option1' checked=''/>
            <div className='btn'>
                <button className='span' onClick={() => handleButtonClick('button1')}>Brazo</button>
            </div>
        </div>
        <div className='option'>
            <input className='input' type='radio' name='btn' value='option2' checked=''/>
            <div className='btn'>
                <button className='span' onClick={() => handleButtonClick('button2')}>Espalda</button>
            </div>
        </div>
        <div className='option'>
            <input className='input' type='radio' name='btn' value='option3' checked=''/>
            <div className='btn'>
                <button className='span' onClick={() => handleButtonClick('button3')}>Gluteo</button>
            </div>
        </div>
        <div className='option'>
            <input className='input' type='radio' name='btn' value='option4' checked=''/>
            <div className='btn'>
                <button className='span' onClick={() => handleButtonClick('button4')}>Pierna</button>
            </div>
        </div>
        {renderCards()}
    </div>
  );
};

export default CardComponent;
