import React, { useState } from 'react';

const Components = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [showFilteredResult, setShowFilteredResult] = useState(false);

  const handleOptionClick = (option) => {
    if (selectedOptions.includes(option)) {
      setSelectedOptions(selectedOptions.filter((item) => item !== option));
    } else {
      setSelectedOptions([...selectedOptions, option])
    }

    setShowFilteredResult(false);
  }

  const handleFilterClick = () => {
    if(selectedOptions.length > 0) {
      setShowFilteredResult(true);
    }
  }

  const options = [
    { id: 1, name: 'Opción 1', cards: ['Tarjeta 1', 'Tarjeta 2', 'Tarjeta 3'] },
    { id: 2, name: 'Opción 2', cards: ['Tarjeta 4', 'Tarjeta 5', 'Tarjeta 6'] },
    { id: 3, name: 'Opción 3', cards: ['Tarjeta 7', 'Tarjeta 8', 'Tarjeta 9'] },
    { id: 4, name: 'Opción 4', cards: ['Tarjeta 10', 'Tarjeta 11', 'Tarjeta 12'] }
  ];
  
  return (
    <div>
      <div>
        
      </div>
      <div className='custom'>
        {options.map((option) => (
          <div className='checkbox' key={option.id} onClick={() => handleOptionClick(option.id)}>
            {option.name}
          </div>
        ))}
        </div>
        <div className='selecteds'>
          <ul className='selected'>
            {selectedOptions.map((optionId) => (
              <li key={optionId}>{options.find((option) => option.id === optionId)?.name}</li>
            ))}
          </ul>
        </div>
      <button onClick={handleFilterClick}>Filtrar</button>
      {showFilteredResult && (
        <div>
          <h3>Resultado Filtrado: </h3>
          {options
            .filter((option) => selectedOptions.includes(option.id))
            .flatMap((option) => option.cards)
            .map((card) => (
              <div key={card}>{card}</div>
            ))
          }
        </div>
      )}
    </div>
  );
  
}

export default Components;
