            {filteredCards.length > 0 ? (
              filteredCards.map((card) => (
                <StoreCards
                  key={card.id}
                  title={card.title}
                  description={card.description}
                  price={card.price}
                  image={card.image}
                  mandarCarrito={() => addToCart(card)}
                />
              ))
            ) : (
