        {cartItems.length > 0 ? (
          <ul>
            {cartItems.map((card) => (
              <StoreCards
                key={card.id}
                title={card.title}
                description={card.description}
                price={card.price}
                image={card.image}
                mandarCarrito={() => removeFromCart(card)}
              />
            ))}
          </ul>
        ) : (
          <p>El carrito está vacío.</p>
        )}
      </div>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Producto añadido al carrito</h3>
            <button onClick={closeModal}>Cerrar</button>
          </div>
        </div>
      )}
