import React, { useState } from "react";

const DataStore = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    }

    const handleCategorySelect = (category) => {
        setSelectedCategory(category);
        searchQuery('');
        setIsOpen(false)
    }

    const handleSearch = (event) => {
        setSearchQuery(event.target.value)
    }

    const cards = [
        { 
            id: 1, 
            title: 'Tarjeta 1', 
            category: 'Categoria 1', 
            description: 'Description 1',
            image: 'image'
        },

        { 
            id: 2, 
            title: 'Tarjeta 2', 
            category: 'Categoria 2',
            description: 'Description 2',
            image: 'image'
        },

        { 
            id: 3, 
            title: 'Tarjeta 3', 
            category: 'Categoria 3',
            description: 'Description 3',
            image: 'image'
        },

        { 
            id: 4, 
            title: 'Tarjeta 4', 
            category: 'Categoria 4',
            description: 'Description 4',
            image: 'image' 
        },
    ]

    const filteredCards = cards.filter((card) => {
        if (selectedCategory && card.category !== selectedCategory) {
            return false;
        }
        if (searchQuery && !card.title.toLowerCase().includes(searchQuery.toLowerCase())) {
            return false;
        }
        return true;
    })

    return (
        <div className="products">
            <button className="btnProducts" onClick={toggleDropdown}>Categorias</button>
            {isOpen && (
                <div>
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={handleSearch}
                        placeholder="Buscar productos"
                    />
                    <ul className="dropdownProducts">
                        <li onClick={() => handleCategorySelect('Categoria 1')}>Categoria 1</li>
                        <li onClick={() => handleCategorySelect('Categoria 2')}>Categoria 2</li>
                        <li onClick={() => handleCategorySelect('Categoria 3')}>Categoria 3</li>
                    </ul>
                </div>
            )}
            {selectedCategory && (
                <div className="selectCategorys">
                    <h3>Tarjetas de la Categoria: {selectedCategory}</h3>
                    {filteredCards.length > 0 ? (
                        <ul>
                            {filteredCards.map((card) => (
                                <li key={card.id}>
                                    <h4>{card.title}</h4>
                                    <p>{card.description}</p>
                                    <img src={card.image} alt={card.title} />
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>No hay tarjetas</p>
                    )}
                </div>
            )}
        </div>
    )
}

export default DataStore;
