import React, {useState} from 'react';
import Modal from 'react-modal';

// Make sure to bind modal to your appElement (https://reactcommunity.org/react-modal/accessibility/)

const ModalWindow = (onClose) => {
    let subtitle;
    const [isFirstModalOpen, setIsFirstModalOpen] = useState(false);
    const [isSecondModalOpen, setIsSecondModalOpen] = useState(false);

    const openFirstModal = () => {
        setIsFirstModalOpen(true)
        setIsSecondModalOpen(false);
    }
    const closeFirstModal = () => {
        setIsFirstModalOpen(false);
    }  

    function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#f00';
  }

  const openSecondModal = () => {
    setIsSecondModalOpen(true);
    setIsFirstModalOpen(false);
}
const closeSecondModal = () => {
    setIsSecondModalOpen(false);
}

  return (
    <div>
        <a onClick={openFirstModal}>Crear Cuenta /</a>
        <a onClick={openSecondModal}> Ingresar</a>
      
        <Modal isOpen={isFirstModalOpen} onClose={closeFirstModal} onAfterOpen={afterOpenModal}>
            <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Primera ventana modal</h2>
            <p>Contenido de la primera ventana modal</p>
            <button onClick={openSecondModal}>Abrir segunda ventana modal</button>
        </Modal>
        <Modal isOpen={isSecondModalOpen} onClose={closeSecondModal} onAfterOpen={afterOpenModal}>
            <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Segunda ventana modal</h2>
            <p>Contenido de la segunda ventana modal</p>
            <button onClick={openFirstModal}>Abrir primera ventana modal</button>
        </Modal>
        <a >Cerrar</a>
    </div>
  );
}

export default ModalWindow;
